INSERT INTO tblproduct (ProductID, CategoryNo, Brand, ProductName, ProductImg, Intro, UnitPrice, PerDiscount, QtyOnHand, TimeCreate) VALUES
(1, 'CPU01', 'AMD', 'CPU AMD Ryzen Threadripper 2970WX (24C/48T, 3.0 GHz - 4.2 GHz, 64MB) - TR4', 'https://lh3.googleusercontent.com/DF4ahxChMtGsJA-EpTo4NE1Zte8xqNM6saYj-xk1mFygnGCVj_wC7v7g4QRVas_o-JOGKGZWOqrxdR3kCxI=w500-rw', 'Được thiết kế để phục vụ nghệ sĩ, biên tập viên, và các kỹ sư, bộ vi xử lý AMD Ryzen Threadripper 2970WX Processor (24-Core, 48-Thread, 4.2 GHz Max Boost) giải phóng sức mạnh \"hoang dã\" nguyên bản từ 24 nhân và 48 luồng xử lý đồng thời. Tối ưu hoá hiệu năng với phần cứng thông minh, AMD SensiMI2 ghi nhớ và điều chỉnh hiệu suất hoạt động cho các chương trình mà bạn sử dụng. Là công cụ đắc lực hỗ trợ Render, thiết kế, chỉnh sửa, sắp xếp, và tạo hình phục vụ các dự án lớn của bạn bằng sức mạnh vô song. Thực hiện đa tác vụ không giới hạn với bộ vi xử lý 24 nhân đầu tiên trên thế giới.', 26300000, 0, 50, '2021-01-12 21:40:28'),
(2, 'CPU01', 'AMD', 'CPU AMD Ryzen 5 3500X (6C/6T, 3.6 GHz up to 4.1 GHz, 32MB) - AM4', 'https://lh3.googleusercontent.com/-UBpJAIylk0T_R5MBxuAoZXp73K_-1mHcFojePjcd6H4hj_4KZbQepGGQgpGS1rATCTWS0htx1-_u7Hu7EM=w500-rw', 'AMD Ryzen 5 3500X là vi xử lý CPU mới tương thích với chipset AMD X570, nền tảng Socket AM4 tiên tiến nhất thế giới dành cho những game thủ và những người đam mê ép xung, cung cấp khả năng kiểm soát toàn diện, mức độ thấp, bao gồm công nghệ tăng tốc lưu trữ AMD StoreMI và hỗ trợ cấu hình card đồ họa kép nhờ hai khe card đồ họa PCIe® 4.0.', 3790000, 0, 50, '2021-01-12 21:40:28'),
